mapper.update:
UPDATE ssrc_rfx_header

    attribute_varchar4=#{attributeVarchar4},
    attribute_varchar5=#{attributeVarchar5},

WHERE rfx_num=#{rfxNum}
AND tenant_id = #{tenantId}

mapper.select:
SELECT file_name fileName,file_size fileSize,file_url fileUrl
FROM    hfle_file
WHERE   attachment_uuid = #{data}

select  Max(t.round_number)
from ssrc_rfx_quotation_header t
where t.rfx_header_id = #{rfxHeaderId}
