mapper.select:
select ei.evaluate_indic_id, ei.tenant_id, ei.source_header_id, ei.source_from, ei.indicate_id, ei.tmpl_assign_id, ei.template_id, ei.weight, ei.qualifie_score, ei.min_score, ei.max_score, ei.team, ei.sequence_num, ei.object_version_number, ei.business_weight, ei.technology_weight, ei.indic_status, ei.indicate_code, ei.indicate_name, ei.indicate_remark, ei.indicate_type, 'ONE' as indicate_level, null as parent_indicate_id, ei.detail_enabled_flag, ei.calculate_type, ei.pass_flag, ei.score_type from ssrc_evaluate_indic ei WHERE ei.tenant_id = #{evaluateIndicDTO.tenantId} AND ei.source_header_id = #{evaluateIndicDTO.sourceHeaderId}
AND ei.indic_status = #{evaluateIndicDTO.indicStatus}
AND team IN ('BUSINESS', 'TECHNOLOGY')
AND team = 'BUSINESS_TECHNOLOGY'
and ei.team = #{evaluateIndicDTO.team}
AND ei.source_from = #{evaluateIndicDTO.sourceFrom}
AND ei.source_from = 'BID'
UNION all select sic.indicate_id evaluate_indic_id, ei.tenant_id, ei.source_header_id, ei.source_from, ei.indicate_id, ei.tmpl_assign_id, ei.template_id, sic.weight, ei.qualifie_score, sic.min_score, sic.max_score, ei.team, ei.sequence_num, ei.object_version_number, ei.business_weight, ei.technology_weight, ei.indic_status, sic.indicate_code, sic.indicate_name, sic.remark indicate_remark, ei.indicate_type, sic.indicate_level, sic.parent_indicate_id, sic.detail_enabled_flag, ei.calculate_type, ei.pass_flag, ei.score_type FROM ssrc_evaluate_indic ei right JOIN ssrc_score_indic sic ON ei.evaluate_indic_id = sic.parent_indicate_id and sic.source_type='MANUAL' and sic.enabled_flag = 1 WHERE ei.tenant_id = #{evaluateIndicDTO.tenantId} AND ei.source_header_id = #{evaluateIndicDTO.sourceHeaderId}
AND ei.indic_status = #{evaluateIndicDTO.indicStatus}
AND team IN ('BUSINESS', 'TECHNOLOGY')
AND team = 'BUSINESS_TECHNOLOGY'
AND sic.indicate_level = #{evaluateIndicDTO.indicateLevel}
and ei.team = #{evaluateIndicDTO.team}
AND ei.source_from = #{evaluateIndicDTO.sourceFrom}
AND ei.source_from = 'BID'

SELECT
    @rowno := @rowno + 1 FileNumber,
    hf.file_name FileName,
    hf.file_size FileSize,
    hf.file_name Description,
    hf.file_url Url
FROM
    hfle_file hf
    LEFT JOIN ssrc_rfx_header srh ON srh.tech_attachment_uuid = hf.attachment_uuid or srh.business_attachment_uuid = hf.attachment_uuid,(
            SELECT
                @rowno := 0
            ) t
WHERE
    srh.tenant_id = #{organizationId}
    AND srh.rfx_header_id = #{rfxHeader.rfxHeaderId}
